{"windows":"// Place your key bindings in this file to override the defaultsauto[]
[
    {
        "key": "ctrl+alt+w",
        "command": "editor.emmet.action.wrapWithAbbreviation"
    },
    {
        "key": "ctrl+numpad8",
        "command": "workbench.action.terminal.resizePaneUp"
    },
    {
        "key": "ctrl+numpad2",
        "command": "workbench.action.terminal.resizePaneDown"
    },
    {
        "key": "ctrl+numpad4",
        "command": "workbench.action.terminal.resizePaneLeft"
    },
    {
        "key": "ctrl+numpad6",
        "command": "workbench.action.terminal.resizePaneRight"
    },
    {
        "key": "alt+numpad5",
        "command": "code-runner.run"
    },
    {
        "key": "ctrl+alt+n",
        "command": "-code-runner.run"
    },
    {
        "key": "ctrl+numpad0",
        "command": "-workbench.action.zoomReset"
    },
    {
        "key": "ctrl+numpad5",
        "command": "extension.CompileRunInExternalTerminal"
    },
    {
        "key": "f8",
        "command": "-extension.CompileRunInExternalTerminal"
    },
    {
        "key": "ctrl+numpad0",
        "command": "workbench.action.output.toggleOutput",
        "when": "workbench.panel.output.active"
    },
    {
        "key": "ctrl+shift+u",
        "command": "-workbench.action.output.toggleOutput",
        "when": "workbench.panel.output.active"
    },
    {
        "key": "ctrl+alt+x",
        "command": "editor.action.commentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+oem_2",
        "command": "-editor.action.commentLine",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+alt+c",
        "command": "editor.action.blockComment",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "shift+alt+a",
        "command": "-editor.action.blockComment",
        "when": "editorTextFocus && !editorReadonly"
    },
    {
        "key": "ctrl+numpad4",
        "command": "workbench.actions.view.problems",
        "when": "workbench.panel.markers.view.active"
    },
    {
        "key": "ctrl+shift+m",
        "command": "-workbench.actions.view.problems",
        "when": "workbench.panel.markers.view.active"
    },
    {
        "key": "ctrl+alt+c",
        "command": "-terminal.stop"
    },
    {
        "key": "ctrl+shift+numpad_add",
        "command": "workbench.action.zoomIn"
    },
    {
        "key": "ctrl+numpad_add",
        "command": "-workbench.action.zoomIn"
    },
    {
        "key": "ctrl+numpad_add",
        "command": "editor.action.fontZoomIn"
    },
    {
        "key": "ctrl+numpad_subtract",
        "command": "editor.action.fontZoomOut"
    },
    {
        "key": "ctrl+oem_minus",
        "command": "-workbench.action.zoomOut"
    },
    {
        "key": "ctrl+oem_plus",
        "command": "-workbench.action.zoomIn"
    },
    {
        "key": "ctrl+shift+numpad_subtract",
        "command": "workbench.action.zoomOut"
    },
    {
        "key": "ctrl+numpad_subtract",
        "command": "-workbench.action.zoomOut"
    },
    {
        "key": "ctrl+alt+backspace",
        "command": "editor.action.deleteLines",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+shift+k",
        "command": "-editor.action.deleteLines",
        "when": "textInputFocus && !editorReadonly"
    },
    {
        "key": "ctrl+alt+right ctrl+alt+right",
        "command": "cursorLineEnd"
    },
    {
        "key": "ctrl+shift+alt+right ctrl+shift+alt+right",
        "command": "cursorLineEndSelect"
    },
    {
        "key": "ctrl+shift+m z",
        "command": "workbench.action.toggleZenMode"
    },
    {
        "key": "ctrl+k z",
        "command": "-workbench.action.toggleZenMode"
    },
    {
        "key": "ctrl+shift+m o",
        "command": "editor.action.revealDefinitionAside",
        "when": "editorHasDefinitionProvider && editorTextFocus && !isInEmbeddedEditor"
    },
    {
        "key": "ctrl+k f12",
        "command": "-editor.action.revealDefinitionAside",
        "when": "editorHasDefinitionProvider && editorTextFocus && !isInEmbeddedEditor"
    },
    {
        "key": "ctrl+alt+numpad6",
        "command": "workbench.action.nextEditor"
    },
    {
        "key": "ctrl+pagedown",
        "command": "-workbench.action.nextEditor"
    },
    {
        "key": "ctrl+alt+right",
        "command": "-workbench.action.moveEditorToNextGroup"
    },
    {
        "key": "ctrl+alt+numpad4",
        "command": "workbench.action.previousEditor"
    },
    {
        "key": "ctrl+pageup",
        "command": "-workbench.action.previousEditor"
    },
    {
        "key": "ctrl+alt+left",
        "command": "-workbench.action.moveEditorToPreviousGroup"
    },
    {
        "key": "ctrl+alt+m tab",
        "command": "workbench.action.focusNextGroup"
    },
    {
        "key": "ctrl+alt+m",
        "command": "-code-runner.stop"
    },
    {
        "key": "ctrl+alt+numpad2",
        "command": "workbench.action.moveEditorToNextGroup"
    },
    {
        "key": "ctrl+alt+numpad8",
        "command": "workbench.action.moveEditorToPreviousGroup"
    },
    {
        "key": "ctrl+alt+left ctrl+alt+left",
        "command": "cursorLineStart"
    },
    {
        "key": "ctrl+shift+alt+left ctrl+shift+alt+left",
        "command": "cursorLineStartSelect"
    },
    {
        "key": "ctrl+alt+m s",
        "command": "workbench.action.toggleStatusbarVisibility"
    },
    {
        "key": "ctrl+alt+numpad5",
        "command": "workbench.action.toggleEditorGroupLayout"
    },
    {
        "key": "shift+alt+0",
        "command": "-workbench.action.toggleEditorGroupLayout"
    },
    {
        "key": "ctrl+alt+numpad7",
        "command": "workbench.action.moveEditorLeftInGroup"
    },
    {
        "key": "ctrl+shift+pageup",
        "command": "-workbench.action.moveEditorLeftInGroup"
    },
    {
        "key": "ctrl+alt+numpad9",
        "command": "workbench.action.moveEditorRightInGroup"
    },
    {
        "key": "ctrl+shift+pagedown",
        "command": "-workbench.action.moveEditorRightInGroup"
    },
    {
        "key": "ctrl+alt+numpad1",
        "command": "workbench.action.toggleEditorWidths"
    },
    {
        "key": "ctrl+alt+numpad3",
        "command": "workbench.action.editorLayoutSingle"
    },
    {
        "key": "ctrl+numpad_decimal",
        "command": "workbench.action.terminal.kill"
    },
    {
        "key": "alt+numpad_decimal",
        "command": "workbench.action.terminal.kill"
    }
]"}
