			"{",
			"    k = 1;",
			"    x--;",
			"    float aux;",
			"    for(int i = 0; i < x; i++){",
			"        if(${2:vecToOrder}[i] ${3} ${2:vecToOrder}[i+1])",
			"        {",
			"            k = 0;",
			"            aux = ${2:vecToOrder}[i];",
			"            ${2:vecToOrder}[i] = ${2:vecToOrder}[i+1];",
			"            ${2:vecToOrder}[i+1] = aux;",
			"        }",
			"    }",
			"}$0"
		],
		"description": "default switch",
	},

